import React, { useState,useEffect } from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Button,
  Row,
  NavLink,
  ButtonGroup,
  Input,
} from "reactstrap";
import urlConfig from "../config/urlsconfig.json";
import config from "../config/config.json";
import { Link, useHistory } from "react-router-dom";
const _ = require("lodash");
const getRole = localStorage.getItem("type");

const ViewCart = ({match}) => {
  console.log("@@@@@@@@", getRole);
  let [orders, updateOrders] = useState("");
  const history = useHistory();
  const userId = localStorage.getItem("userId");
  let [product, setProduct] = useState([]);
  let [Userid, setUserId] = useState("");
  let [price, setPrice] = useState("");

  const changeProduct = (e) => {
    setProduct((product = e.target.value));
  };
  const changeUserId = (e) => {
    setUserId((Userid = e.target.value));
  };
  const changePrice = (e) => {
    setPrice((price = e.target.value));
  };


  const token = localStorage.getItem("token");

  let url = `${config.baseURL}${urlConfig.editorder.uri}`;
  url = url.replace(":id", match.params.id);
  url = url.replace(":userId", userId);
  console.log("url", url);
  fetch(url, {
    method: urlConfig.editorder.method,
    headers: {
      "Content-Type": "application/json",
      Authorization: `bearer ${token}`,
      type: getRole,
    },
  }).then((res) => {
    if (res.status === 200) {
      return res.json();
    }
  });
  const fetchOrder = () => {
    let url = `${config.baseURL}${urlConfig.getorder.uri}`;
    url = url.replace(":id",localStorage.getItem("orderId") );
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.getorder.method,
      headers: {
        "Content-Type": "application/json",
        "Authorization": `bearer ${token}`,
        "type": getRole
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        if (response) {
          setProduct(response.products);
          setUserId(response.userId);
          setPrice(response.unitPrice);
        }
      });
  };
  useEffect(() => {
    fetchOrder(match.params.id);
  }, []);

  const deleteOrder = (id) => {
    let url = `${config.baseURL}${urlConfig.deleteorder.uri}`;
    url = url.replace(":id", id);
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.deleteorder.method,
      headers: {
        "Context-Type": "application/json",
        Authorization: `bearer ${token}`,
        type: getRole,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        let _orders = _.clone(orders);
        _.remove(_orders, (order) => {
          if (order._id === id) return true;
        });
        updateOrders((orders = _orders));
        window.location.reload(false);
      });
  };
  return (
    <div>
    <br/>
      <React.Fragment>
        <Card>
          <CardBody>
            <hr />
            <CardTitle>
              <h5 className="display-7" > Product Name :{} </h5>
            </CardTitle>
            <hr />
            <CardSubtitle>
              <h6 className="display-8"> Description : </h6>
            </CardSubtitle>
            <hr />
            <CardText>
              <p className="lead-small">Price : Rs  /-</p>
            </CardText>
            <hr />
            <CardText>
              <p className="lead-small">Quantity :  </p>
            </CardText>
            <CardText>
            <h4>Total Price : </h4>
            </CardText>
            <h4>
                <ButtonGroup>
                  <Button onClick={() => deleteOrder(match.params.id)}>
                    Remove Item
                  </Button>
                </ButtonGroup>
              
            </h4>
            <p></p>
          </CardBody>
        </Card>
      </React.Fragment>
    </div>
  );
};
export default ViewCart;
