import React, { useState,useEffect } from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Button,
  Row,
  NavLink,
  ButtonGroup,
  Input,
  Col,
  Form,
} from "reactstrap";
import urlConfig from "../config/urlsconfig.json";
import config from "../config/config.json";
import { Link, useHistory } from "react-router-dom";
const _ = require("lodash");
const getRole = localStorage.getItem("type");

const ViewCart = ({match}) => {
  console.log("@@@@@@@@", getRole);
  let [orders, updateOrders] = useState("");
  const history = useHistory();
  const userId = localStorage.getItem("userId");
  let [products, updateProducts] = useState([]);
  let [productName, setProductName] = useState("");
  let [productImage, setProductImage] = useState("");
  let [description, setDescription] = useState("");
  let [quantity, setQuantity] = useState("");
  let [unitPrice, setUnitPrice] = useState(""); 

  const changeProductName = (e) => {
    setProductName((productName = e.target.value));
  };
  const changeProductImage = (e) => {
    setProductImage((productImage = e.target.value));
  };
  const changeDescription = (e) => {
    setDescription((description = e.target.value));
  };
  const changeQuantity = (e) => {
    setQuantity((quantity = e.target.value));
  };
  const changeUnitPrice = (e) => {
    setUnitPrice((unitPrice = e.target.value));
  };


  const token = localStorage.getItem("token");

  let url = `${config.baseURL}${urlConfig.editorder.uri}`;
  url = url.replace(":id", match.params.id);
  url = url.replace(":userId", userId);
  console.log("url", url);
  fetch(url, {
    method: urlConfig.editorder.method,
    headers: {
      "Content-Type": "application/json",
      Authorization: `bearer ${token}`,
      type: getRole,
    },
  }).then((res) => {
    if (res.status === 200) {
      return res.json();
    }
  });
  const fetchProduct = (id) => {
    let url = `${config.baseURL}${urlConfig.getproduct.uri}`;
    url = url.replace(":id", id);
    console.log("111111111111111111111111111",id);
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.getproduct.method,
      headers: {
        "Content-Type": "application/json",
        "Authorization": `bearer ${token}`,
        "type": getRole
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        if (response) {
          setProductName(response.productName);
          setProductImage(response.productImage);
          setDescription(response.description);
          setUnitPrice(response.unitPrice);
          setQuantity(response.quantity)
        }
      });
  };
  useEffect(() => {
    fetchProduct(match.params.id);
  }, []);

  const deleteOrder = (id) => {
    let url = `${config.baseURL}${urlConfig.deleteorder.uri}`;
    url = url.replace(":id", id);
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.deleteorder.method,
      headers: {
        "Context-Type": "application/json",
        Authorization: `bearer ${token}`,
        type: getRole,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        let _orders = _.clone(orders);
        _.remove(_orders, (order) => {
          if (order._id === id) return true;
        });
        updateOrders((orders = _orders));
        window.location.reload(false);
      });
  };
  return (
    <div>
    <br />
    <br />
    <br />
    <br />
    <Row>
      <Col xs="1"></Col>
      <Col xs="10">
        <Card>
          <CardBody>
            <Form>
              <FormGroup>
                <InputGroup size="sm">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>PRODUCT NAME</InputGroupText>
                  </InputGroupAddon>
                  <Input
                    size="sm"
                    type="text"
                    name="title"
                    id="title"
                    onChange={changeProductName}
                    autoComplete="off"
                  />
                </InputGroup>
                <br />
                <InputGroup size="sm">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>PRODUCT IMAGE</InputGroupText>
                  </InputGroupAddon>
                  <Input
                    size="sm"
                    type="subtitle"
                    name="subtitle"
                    id="subtitle"
                    onChange={changeProductImage}
                  />
                </InputGroup>
                <br />
                <InputGroup size="sm">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>DESCRIPTION</InputGroupText>
                  </InputGroupAddon>
                  <Input
                    size="sm"
                    type="text"
                    name="url"
                    id="url"
                    onChange={changeDescription}
                  />
                </InputGroup>
                <br />
                <InputGroup size="sm">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>UNIT PRICE</InputGroupText>
                  </InputGroupAddon>
                </InputGroup>
                <Input
                  size="sm"
                  type="number"
                  name="description"
                  id="description"
                  onChange={changeUnitPrice}
                />
                <br />
                <InputGroup size="sm">
                  <InputGroupAddon addonType="prepend">
                    <InputGroupText>QUANTITY</InputGroupText>
                  </InputGroupAddon>
                </InputGroup>
                <Input
                  size="sm"
                  type="number"
                  name="description"
                  id="description"
                  onChange={changeQuantity}
                />

                <br />
                <Button color="info" size="sm" onClick={deleteOrder}>
                  Remove Item
                </Button>
              </FormGroup>
            </Form>
          </CardBody>
        </Card>
      </Col>
      <Col xs="1"></Col>
    </Row>
  </div>
  )
};
export default ViewCart;
