import React, { useState } from "react";
import {
  Card,
  CardBody,
  CardFooter,
  CardImg,
  CardSubtitle,
  CardText,
  CardTitle,
  Button,
  Row,
  NavLink,
  ButtonGroup,
  Input,
} from "reactstrap";
import urlConfig from "../config/urlsconfig.json";
import config from "../config/config.json";
import { Link, useHistory } from "react-router-dom";
// import {
//   AiOutlineLike,
//   AiFillLike,
//   AiOutlineEdit,
//   AiOutlineDelete,       
// } from "react-icons/ai";
const _ = require("lodash");
const getRole = localStorage.getItem("type");

const ProductCard = (props) => {
    console.log("@@@@@@@@",getRole);
  const history = useHistory();
  const userId = localStorage.getItem("userId");
  let [products, updateProducts] = useState([]);

  console.log("!!!!!!!!!!!!!!!!!!!!!!!!!!!!!!", props._id)

  const token = localStorage.getItem("token");

  let url = `${config.baseURL}${urlConfig.editproduct.uri}`;
  url = url.replace(":id", props._id);
  url = url.replace(":userId", userId);
  console.log("url",url)
  fetch(url, {
    method: urlConfig.editproduct.method,
    headers: {
      "Content-Type": "application/json",
      "Authorization": `bearer ${token}`,
      "type": getRole,
    },
  }).then((res) => {
    if (res.status === 200) {
      return res.json();
    }
  });
  const deleteProduct = (id) => {
    let url = `${config.baseURL}${urlConfig.deleteproduct.uri}`;
    url = url.replace(":id", id);
    url = url.replace(":userId", userId);
    fetch(url, {
      method: urlConfig.deleteproduct.method,
      headers: {
        "Context-Type": "application/json",
        "Authorization": `bearer ${token}`,
        "type": getRole,
      },
    })
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((response) => {
        let _products = _.clone(products);
        _.remove(_products, (product) => {
          if (product._id === id) return true;
        });
        updateProducts((products = _products));
        window.location.reload(false);
      });
  };
  return (
    <div>
      <React.Fragment>
        <Card>
          <CardBody>
            <CardImg width="200px" height="200px" src={props.productImage} />
            <hr/>   
            <CardTitle>
              <h5 className="display-7"> Product Name : {props.productName}</h5>
            </CardTitle>
            <hr/>
            <CardSubtitle>
              <h6 className="display-8"> Description : {props.description}</h6>
            </CardSubtitle>
            <hr />
            <CardText>
              <p className="lead-small">Price : {props.unitPrice}</p>
            </CardText>
            <hr />
            <CardText>
              <p className="lead-small">Quantity : Rs {props.quantity} /-</p>
            </CardText>
            <h4>
           {
               
           }
                <ButtonGroup>
                  <Button
                    onClick={() => history.push(`/editproduct/${props._id}`)}
                  >Edit</Button>

                  <Button onClick={() => deleteProduct(props._id)}>Delete</Button>
                </ButtonGroup>
         
             
            </h4>
            <p></p>
          </CardBody>
        </Card>
      </React.Fragment>
    </div>
  );
};
export default ProductCard;
